  //  }
    return (
        <>
        <div className='bg'>
        <Navbar/>
            <form1>
  <div className="container1">
    <h1 className='h1Style'>Sign up</h1>
    <div className="horizontal rule">

    <label><b>First Name</b></label>
    <input className='input1' type="text" placeholder="Enter First Name" name="fname" id="fname" /*ref={fnameRef}*/ required/>

    <label><b>Last Name</b></label>
    <input className='input1' type="text" placeholder="Enter Last Name" name="lname" id="lname" ref={lnameRef} required/>

    <label><b>Email</b></label>
    <input className='input1' type="text" placeholder="Enter Email" name="email" id="email" ref={emailRef} required/>

    <label><b>Employee Id</b></label>
    <input className='input1' type="text" placeholder="Enter Employee Id" name="eid" id="eid" ref={eidRef} required/>

    <label><b>Department</b></label>
    <input className='input1' type="text" placeholder="Enter Department" name="dept" id="dept" ref={deptRef} required/>

    <label><b>Mobile Number</b></label>
    <input className='input1' type="tel" placeholder="Enter Mobile no." name="mno" id="mno" ref={mnoRef} required/>
    
    <label><b>Password</b></label>
    <input className='input1' type="password" placeholder="Enter Password" name="psw" id="psw" ref={passwordRef} required/>

    <label><b>Confirm Password</b></label>
    <input className='input1' type="password" placeholder="Confirm Password" name="psw-confirm" id="psw-confirm" ref={passwordConfirmRef} required/>

    </div>

    

    <button type="submit" className="registerbtn">Submit</button>
    <div className="signin">
    <p>Already have an account? <Link to="/login">Sign in</Link></p>
    
  </div>
  </div>
  
  
</form1>
</div>
<Footer/>
        </>
    )
}

export default Signup
